import { Component,  OnInit } from '@angular/core';
import { AngularRecrutadoresService } from 'src/app/shared/angular-recrutadores.service'
import { AngularRecrutadores } from 'src/app/shared/angular-recrutadores.model';
import { NgForm } from '@angular/forms';

@Component({
  selector: 'app-angular-recrutadores-form',
  templateUrl: './angular-recrutadores-form.component.html',
  styleUrls: ['./angular-recrutadores-form.component.css']
})
export class AngularRecrutadoresFormComponent {

  constructor(public service:AngularRecrutadoresService){}
  ngOnInit(): void {}

  resetForm(form: NgForm) {
    form.form.reset();
    this.service.formData = new AngularRecrutadores();
  }

  onSubmit(form: NgForm) {
    if (this.service.formData.paymentDetailId == 0)
      this.insertRecord(form);
    else
      this.updateRecord(form);
  }

}
<form novalidate>
  <input type="hidden" name="id" [value]="service.formData.id" />
  <div class="form-row">
    <div class="form-group col-md-12">
      <label>Nome</label>
      <input type="text" class="form-control form-control-lg" placeholder="Full Name" name="nome" #nome="ngModel"
        [(ngModel)]="service.formData.nome" required [class.invalid]="nome.invalid && nome.touched" />
    </div>

    <div class="form-group col-md-12">
      <label>Email</label>
      <input type="text" class="form-control form-control-lg" placeholder="Full Name" name="email" #email="ngModel"
        [(ngModel)]="service.formData.email" required [class.invalid]="email.invalid && email.touched" />
    </div>
    <div class="form-group col-md-12">
      <label>Empresa</label>
      <input type="text" class="form-control form-control-lg" placeholder="Full Name" name="empresa" #empresa="ngModel"
        [(ngModel)]="service.formData.empresa" required [class.invalid]="empresa.invalid && empresa.touched" />
    </div>
  </div>
  <div class="form-group">
    <button type="submit" class="btn btn-info btn-lg btn-block" [disabled]="form.invalid">Submit</button>
  </div>
</form>
